import React, { useState, useEffect } from 'react';
import axios from 'axios';
import QuizForm from './QuizForm';
import QuestionForm from './QuestionForm';
import QuizList from './QuizList';
import './ModernUI.css';

const EnhancedAdminDashboard = ({ 
  onManageQuizzes, 
  onQuestionBank, 
  onAllResults, 
  onUserAccounts,
  onLeaderboard,
  onQuizCreated,
  onQuestionAdded,
  onSelectQuiz,
  onViewQuestions,
  onDeleteQuiz,
  selectedQuizId,
  quizListKey
}) => {
  const [showCreateQuiz, setShowCreateQuiz] = useState(false);
  const [showAddQuestion, setShowAddQuestion] = useState(false);
  const [selectedQuizForQuestion, setSelectedQuizForQuestion] = useState(null);
  const [availableQuizzes, setAvailableQuizzes] = useState([]);

  useEffect(() => {
    loadQuizzes();
  }, []);

  useEffect(() => {
    loadQuizzes();
  }, [quizListKey]);

  const loadQuizzes = async () => {
    try {
      const BASE_URL = 'http://localhost:8080';
      const response = await axios.get(`${BASE_URL}/api/quizzes`);
      setAvailableQuizzes(response.data);
    } catch (error) {
      const localQuizzes = JSON.parse(localStorage.getItem('quizzes') || '[]');
      setAvailableQuizzes(localQuizzes);
    }
  };

  const QuizSelector = ({ onQuizSelected }) => (
    <div className="modern-form-group" style={{ marginBottom: '1.5rem' }}>
      <label className="modern-form-label">Select Quiz to Add Questions:</label>
      <select 
        className="modern-form-select"
        onChange={(e) => onQuizSelected(e.target.value)}
        defaultValue=""
      >
        <option value="" disabled>Choose a quiz...</option>
        {availableQuizzes.map(quiz => (
          <option key={quiz.id} value={quiz.id}>{quiz.title}</option>
        ))}
      </select>
    </div>
  );
  const [adminStats, setAdminStats] = useState({
    totalQuizzes: 0,
    totalQuestions: 0,
    totalStudents: 0,
    totalAttempts: 0,
    recentActivity: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadAdminData();
  }, []);

  useEffect(() => {
    loadAdminData();
  }, [quizListKey]);

  useEffect(() => {
    const handleQuestionsUpdated = () => {
      loadAdminData();
    };
    window.addEventListener('questionsUpdated', handleQuestionsUpdated);
    return () => window.removeEventListener('questionsUpdated', handleQuestionsUpdated);
  }, []);

  const loadAdminData = async () => {
    try {
      const quizzes = JSON.parse(localStorage.getItem('quizzes') || '[]');
      const students = JSON.parse(localStorage.getItem('students') || '[]');
      const attempts = JSON.parse(localStorage.getItem('quizAttempts') || '[]');
      const deletedQuestions = JSON.parse(localStorage.getItem('deletedQuestions') || '[]');

      console.log('Loading admin data:', { quizzes, students, attempts, deletedQuestions });

      // Count questions from localStorage first, then API as fallback
      let totalQuestions = 0;

      // First count localStorage questions
      quizzes.forEach(quiz => {
        if (quiz.questions) {
          const activeQuestions = quiz.questions.filter(q => !deletedQuestions.includes(q.id));
          totalQuestions += activeQuestions.length;
        }
      });

      // If no localStorage questions, try API
      if (totalQuestions === 0) {
        for (const quiz of quizzes) {
          try {
            const BASE_URL = 'http://localhost:8080';
            const questionsResponse = await axios.get(`${BASE_URL}/api/quizzes/${quiz.id}/questions`);
            const activeApiQuestions = questionsResponse.data.filter(q => !deletedQuestions.includes(q.id));
            totalQuestions += activeApiQuestions.length;
          } catch (qErr) {
            console.log(`Failed to fetch questions for quiz ${quiz.id}`);
          }
        }
      }

      // Get recent activity (last 10 attempts)
      const recentActivity = attempts
        .sort((a, b) => new Date(b.completedAt) - new Date(a.completedAt))
        .slice(0, 10);

      const newStats = {
        totalQuizzes: quizzes.length,
        totalQuestions,
        totalStudents: students.length,
        totalAttempts: attempts.length,
        recentActivity
      };

      console.log('New admin stats:', newStats);
      setAdminStats(newStats);
      setLoading(false);
    } catch (error) {
      console.error('Error loading admin data:', error);
      setLoading(false);
    }
  };



  if (loading) {
    return (
      <div className="modern-loading">
        <div className="modern-spinner"></div>
        Loading admin dashboard...
      </div>
    );
  }



  return (
    <div className="enhanced-admin-dashboard" style={{ display: 'flex', height: '100vh', width: '100%', overflow: 'hidden' }}>
      {/* Left Sidebar - Stats (20%) */}
      <div className="admin-left-sidebar" style={{ 
        width: '20%', 
        backgroundColor: '#f8fafc', 
        padding: '1.5rem 1rem', 
        display: 'flex', 
        flexDirection: 'column', 
        gap: '1rem',
        borderRight: '1px solid #e5e7eb',
        overflow: 'hidden'
      }}>
        <div style={{
          backgroundColor: '#3b82f6',
          borderRadius: '12px',
          padding: '1.5rem',
          color: 'white',
          textAlign: 'center',
          boxShadow: '0 4px 6px rgba(59, 130, 246, 0.3)'
        }}>
          <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>üìù</div>
          <div style={{ fontSize: '2rem', fontWeight: '700' }}>{adminStats.totalQuizzes}</div>
          <div style={{ fontSize: '0.875rem', opacity: 0.9 }}>Total Quizzes</div>
        </div>
        <div style={{
          backgroundColor: '#10b981',
          borderRadius: '12px',
          padding: '1.5rem',
          color: 'white',
          textAlign: 'center',
          boxShadow: '0 4px 6px rgba(16, 185, 129, 0.3)'
        }}>
          <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>‚ùì</div>
          <div style={{ fontSize: '2rem', fontWeight: '700' }}>{adminStats.totalQuestions}</div>
          <div style={{ fontSize: '0.875rem', opacity: 0.9 }}>Total Questions</div>
        </div>
        <div style={{
          backgroundColor: '#f59e0b',
          borderRadius: '12px',
          padding: '1.5rem',
          color: 'white',
          textAlign: 'center',
          boxShadow: '0 4px 6px rgba(245, 158, 11, 0.3)'
        }}>
          <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>üë•</div>
          <div style={{ fontSize: '2rem', fontWeight: '700' }}>{adminStats.totalStudents}</div>
          <div style={{ fontSize: '0.875rem', opacity: 0.9 }}>Students</div>
        </div>
        <div style={{
          backgroundColor: '#8b5cf6',
          borderRadius: '12px',
          padding: '1.5rem',
          color: 'white',
          textAlign: 'center',
          boxShadow: '0 4px 6px rgba(139, 92, 246, 0.3)'
        }}>
          <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>üéØ</div>
          <div style={{ fontSize: '2rem', fontWeight: '700' }}>{adminStats.totalAttempts}</div>
          <div style={{ fontSize: '0.875rem', opacity: 0.9 }}>Quiz Attempts</div>
        </div>
      </div>

      {/* Right Main Content (80%) */}
      <div className="admin-main-content" style={{ 
        width: '80%', 
        padding: '1.5rem',
        overflowY: 'auto',
        height: '100vh'
      }}>
        {/* Welcome Section */}
        <div className="modern-card">
          <h2 style={{ color: 'var(--text-primary)', marginBottom: '1.5rem' }}>
            Administrative Interface
          </h2>
        </div>

        {/* Navigation */}
        <div className="modern-nav" style={{ display: 'grid', gridTemplateColumns: 'repeat(5, 1fr)', gap: '1rem', width: '100%' }}>
          <button className="modern-btn" onClick={onManageQuizzes} style={{ backgroundColor: '#dbeafe', color: '#1e40af', border: '1px solid #bfdbfe', padding: '1rem', fontSize: '1rem', width: '100%' }}>
            üìù Manage Quizzes
          </button>
          <button className="modern-btn" onClick={onQuestionBank} style={{ backgroundColor: '#d1fae5', color: '#065f46', border: '1px solid #a7f3d0', padding: '1rem', fontSize: '1rem', width: '100%' }}>
            üè¶ Question Bank
          </button>
          <button className="modern-btn" onClick={onAllResults} style={{ backgroundColor: '#fef3c7', color: '#92400e', border: '1px solid #fde68a', padding: '1rem', fontSize: '1rem', width: '100%' }}>
            üìä Student Results
          </button>
          <button className="modern-btn" onClick={onLeaderboard} style={{ backgroundColor: '#fdf2f8', color: '#be185d', border: '1px solid #fbcfe8', padding: '1rem', fontSize: '1rem', width: '100%' }}>
            üèÜ Class Rankings
          </button>
          <button className="modern-btn" onClick={onUserAccounts} style={{ backgroundColor: '#ede9fe', color: '#5b21b6', border: '1px solid #c4b5fd', padding: '1rem', fontSize: '1rem', width: '100%' }}>
            üë• User Accounts
          </button>
        </div>



        {/* Quick Actions */}
        <div className="modern-card">
          <h3 style={{ color: 'var(--text-primary)', marginBottom: '1rem' }}>
            Quick Actions
          </h3>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '1rem' }}>
            <div className="modern-card" style={{ margin: 0, padding: '1rem', textAlign: 'center' }}>
              <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>üìù</div>
              <h4 style={{ margin: '0 0 0.5rem 0', color: 'var(--text-primary)' }}>Create Quiz</h4>
              <p style={{ margin: '0 0 1rem 0', fontSize: '0.875rem', color: 'var(--text-secondary)' }}>
                Start building a new quiz
              </p>
              <button className="modern-btn modern-btn-primary" onClick={() => setShowCreateQuiz(!showCreateQuiz)}>
                {showCreateQuiz ? 'Hide Form' : 'Get Started'}
              </button>
            </div>

            <div className="modern-card" style={{ margin: 0, padding: '1rem', textAlign: 'center' }}>
              <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>‚ùì</div>
              <h4 style={{ margin: '0 0 0.5rem 0', color: 'var(--text-primary)' }}>Add Questions</h4>
              <p style={{ margin: '0 0 1rem 0', fontSize: '0.875rem', color: 'var(--text-secondary)' }}>
                Build your question bank
              </p>
              <button className="modern-btn modern-btn-primary" onClick={() => setShowAddQuestion(!showAddQuestion)}>
                {showAddQuestion ? 'Hide Form' : 'Add Questions'}
              </button>
            </div>

            <div className="modern-card" style={{ margin: 0, padding: '1rem', textAlign: 'center' }}>
              <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>üë•</div>
              <h4 style={{ margin: '0 0 0.5rem 0', color: 'var(--text-primary)' }}>Manage Users</h4>
              <p style={{ margin: '0 0 1rem 0', fontSize: '0.875rem', color: 'var(--text-secondary)' }}>
                View and manage students
              </p>
              <button className="modern-btn modern-btn-primary" onClick={onUserAccounts}>
                Manage Users
              </button>
            </div>
          </div>
        </div>

        {/* Inline Forms */}
        {showCreateQuiz && (
          <div className="modern-card">
            <h3 style={{ color: 'var(--text-primary)', marginBottom: '1rem' }}>Create New Quiz</h3>
            <QuizForm onQuizCreated={(quiz) => { onQuizCreated(quiz); setShowCreateQuiz(false); loadQuizzes(); }} />
          </div>
        )}

        {showAddQuestion && (
          <div className="modern-card">
            <h3 style={{ color: 'var(--text-primary)', marginBottom: '1rem' }}>Add New Question</h3>
            <QuizSelector onQuizSelected={(quizId) => {
              // Show question form for selected quiz
              setSelectedQuizForQuestion(quizId);
            }} />
            {selectedQuizForQuestion && (
              <QuestionForm 
                quizId={selectedQuizForQuestion} 
                onQuestionAdded={(question) => { 
                  onQuestionAdded(question); 
                  setShowAddQuestion(false);
                  setSelectedQuizForQuestion(null);
                }} 
              />
            )}
          </div>
        )}

        {/* Available Quizzes */}
        <div className="modern-card">
          <QuizList
            key={quizListKey}
            onSelectQuiz={onSelectQuiz}
            onViewQuestions={onViewQuestions}
            onDeleteQuiz={onDeleteQuiz}
            userRole="admin"
          />
          {selectedQuizId && (
            <div style={{ marginTop: '1rem' }}>
              <h4 style={{ color: 'var(--text-primary)' }}>Add Questions to Selected Quiz</h4>
              <QuestionForm quizId={selectedQuizId} onQuestionAdded={onQuestionAdded} />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default EnhancedAdminDashboard;
