import React, { useState, useEffect } from 'react';
import './ModernUI.css';

const Login = ({ onLogin }) => {
  const [credentials, setCredentials] = useState({ username: '', password: '', email: '' });
  const [error, setError] = useState('');
  const [isSignup, setIsSignup] = useState(false);
  const [students, setStudents] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [showAdminModal, setShowAdminModal] = useState(false);
  const [adminCredentials, setAdminCredentials] = useState({ username: '', password: '' });
  const [adminError, setAdminError] = useState('');
  const [adminLoading, setAdminLoading] = useState(false);

  // Load students from localStorage on component mount
  useEffect(() => {
    const savedStudents = localStorage.getItem('students');
    if (savedStudents) {
      setStudents(JSON.parse(savedStudents));
    }
  }, []);

  // Save students to localStorage
  const saveStudents = (studentList) => {
    localStorage.setItem('students', JSON.stringify(studentList));
    setStudents(studentList);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    // Simulate loading for better UX
    await new Promise(resolve => setTimeout(resolve, 600));

    // Remove admin login from student form - now handled by modal

    if (isSignup) {
      // Student signup
      if (credentials.username.length < 3) {
        setError('Username must be at least 3 characters');
        setIsLoading(false);
        return;
      }
      if (credentials.password.length < 6) {
        setError('Password must be at least 6 characters');
        setIsLoading(false);
        return;
      }

      // Check if username is deleted or deactivated
      const userStatuses = JSON.parse(localStorage.getItem('userStatuses') || '{}');
      const userStatus = userStatuses[credentials.username];

      if (userStatus?.deleted) {
        setError('This username has been permanently deleted. Please contact administrator.');
        setIsLoading(false);
        return;
      }

      if (userStatus?.active === false) {
        setError('This username has been deactivated. Please contact administrator.');
        setIsLoading(false);
        return;
      }

      if (students.find(s => s.username === credentials.username)) {
        setError('Username already exists');
        setIsLoading(false);
        return;
      }

      if (students.find(s => s.email === credentials.email)) {
        setError('Email already exists');
        setIsLoading(false);
        return;
      }

      const newStudent = { username: credentials.username, password: credentials.password, email: credentials.email };
      const updatedStudents = [...students, newStudent];
      saveStudents(updatedStudents);
      localStorage.setItem('username', credentials.username);
      console.log('Signup: Saved username to localStorage:', credentials.username);
      setIsLoading(false);
      onLogin();
    } else {
      // Student login - check user status first
      const userStatuses = JSON.parse(localStorage.getItem('userStatuses') || '{}');
      const userStatus = userStatuses[credentials.username];

      if (userStatus?.deleted) {
        setError('This account has been deleted. Please contact administrator.');
        setIsLoading(false);
        return;
      }

      if (userStatus?.active === false) {
        setError('This account has been deactivated. Please contact administrator.');
        setIsLoading(false);
        return;
      }

      const student = students.find(s => s.username === credentials.username && s.password === credentials.password);
      if (student) {
        localStorage.setItem('username', credentials.username);
        console.log('Login: Saved username to localStorage:', credentials.username);
        setIsLoading(false);
        onLogin();
      } else {
        setError('Invalid credentials');
        setIsLoading(false);
      }
    }
  };

  const handleAdminLogin = async (e) => {
    e.preventDefault();
    setAdminError('');
    setAdminLoading(true);

    await new Promise(resolve => setTimeout(resolve, 300));

    if (adminCredentials.username === 'admin' && adminCredentials.password === 'admin123') {
      localStorage.setItem('username', 'admin');
      localStorage.setItem('userRole', 'admin');
      setAdminLoading(false);
      setShowAdminModal(false);
      onLogin('admin');
    } else {
      setAdminError('Invalid admin credentials');
      setAdminLoading(false);
    }
  };

  return (
    <div className="login-page-wide" style={{
      marginTop: 0,
      paddingTop: 0,
      height: 'calc(100vh - 156px)'
    }}>
      <div className="login-left-panel" style={{
        background: 'linear-gradient(135deg, #3b82f6 0%, #1e40af 100%)'
      }}>
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          height: '100%',
          color: 'white',
          textAlign: 'center',
          padding: '2rem',
          position: 'relative'
        }}>
          <button
            onClick={() => setShowAdminModal(true)}
            style={{
              position: 'absolute',
              top: '20px',
              right: '20px',
              background: 'rgba(255,255,255,0.3)',
              border: '2px solid rgba(255,255,255,0.5)',
              borderRadius: '12px',
              color: 'white',
              padding: '0.75rem 1rem',
              fontSize: '0.875rem',
              fontWeight: '600',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem',
              transition: 'all 0.2s ease',
              boxShadow: '0 2px 8px rgba(0,0,0,0.2)',
              zIndex: 10
            }}
            onMouseOver={(e) => {
              e.target.style.background = 'rgba(255,255,255,0.4)';
              e.target.style.transform = 'scale(1.05)';
            }}
            onMouseOut={(e) => {
              e.target.style.background = 'rgba(255,255,255,0.3)';
              e.target.style.transform = 'scale(1)';
            }}
          >
            ðŸ‘† Admin
          </button>
          <div style={{
            fontSize: '4rem',
            marginBottom: '1rem'
          }}>ðŸŽ“</div>
          <h1 style={{
            fontSize: '3rem',
            fontWeight: 'bold',
            marginBottom: '1rem',
            textShadow: '0 2px 4px rgba(0,0,0,0.3)'
          }}>QuizMaster</h1>
          <p style={{
            fontSize: '1.2rem',
            opacity: 0.9,
            maxWidth: '300px',
            lineHeight: '1.6'
          }}>Test Your Knowledge, Track Your Growth</p>
        </div>
      </div>

      <div className="login-right-panel" style={{
        background: '#ffffff'
      }}>
        <div className="login-form-container">
          <div className="form-header" style={{
            transform: isSignup ? 'translateY(-10px)' : 'translateY(0)',
            transition: 'all 0.6s cubic-bezier(0.4, 0, 0.2, 1)'
          }}>
            <h2 style={{
              transform: isSignup ? 'scale(1.05)' : 'scale(1)',
              transition: 'all 0.5s cubic-bezier(0.4, 0, 0.2, 1)'
            }}>{isSignup ? 'Create Account' : 'Welcome Back'}</h2>
            <p style={{
              opacity: isSignup ? 0.9 : 0.7,
              transition: 'opacity 0.4s ease'
            }}>{isSignup ? 'Start your learning journey today' : 'Please sign in to your account'}</p>
          </div>

          <form onSubmit={handleSubmit} className="wide-login-form" style={{
            transform: isSignup ? 'translateX(5px)' : 'translateX(0)',
            transition: 'transform 0.5s cubic-bezier(0.4, 0, 0.2, 1)'
          }}>
            {isSignup && (
              <div className="input-group" style={{
                opacity: isSignup ? 1 : 0,
                transform: isSignup ? 'translateY(0) scale(1)' : 'translateY(-20px) scale(0.95)',
                maxHeight: isSignup ? '80px' : '0',
                overflow: 'hidden',
                transition: 'all 0.6s cubic-bezier(0.4, 0, 0.2, 1)'
              }}>
                <label>Email</label>
                <input
                  type="email"
                  value={credentials.email}
                  onChange={(e) => setCredentials({...credentials, email: e.target.value})}
                  placeholder="Enter your email"
                  required
                />
              </div>
            )}

            <div className="input-group" style={{
              transform: isSignup ? 'translateY(0)' : 'translateY(0)',
              transition: 'transform 0.4s cubic-bezier(0.4, 0, 0.2, 1)'
            }}>
              <label>Username</label>
              <input
                type="text"
                value={credentials.username}
                onChange={(e) => setCredentials({...credentials, username: e.target.value})}
                placeholder="Enter your username"
                required
              />
            </div>

            <div className="input-group" style={{
              transform: isSignup ? 'translateY(0)' : 'translateY(0)',
              transition: 'transform 0.5s cubic-bezier(0.4, 0, 0.2, 1)'
            }}>
              <label>Password</label>
              <input
                type="password"
                value={credentials.password}
                onChange={(e) => setCredentials({...credentials, password: e.target.value})}
                placeholder="Enter your password"
                required
              />
            </div>

            {error && (
              <div className="error-alert">
                <svg viewBox="0 0 24 24" fill="currentColor">
                  <path d="M12 2C6.48 2 2 6.48 2 12S6.48 22 12 22 22 17.52 22 12 17.52 2 12 2ZM13 17H11V15H13V17ZM13 13H11V7H13V13Z"/>
                </svg>
                {error}
              </div>
            )}

            <button type="submit" className={`submit-btn ${isLoading ? 'loading' : ''}`} disabled={isLoading} style={{
              transform: isSignup ? 'scale(1.02)' : 'scale(1)',
              transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)'
            }}>
              {isLoading ? (
                <div className="loading-spinner"></div>
              ) : (
                isSignup ? 'Signup' : 'Sign In'
              )}
            </button>
          </form>



          <div className="form-footer" style={{
            transform: isSignup ? 'translateY(10px)' : 'translateY(0)',
            transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)'
          }}>
            <p>
              {isSignup ? 'Already have an account?' : 'New Student?'}
              <button 
                type="button" 
                onClick={() => {
                  setIsSignup(!isSignup);
                  setError('');
                  setCredentials({ username: '', password: '', email: '' });
                }}
                className="switch-btn"
                style={{
                  transform: isSignup ? 'scale(1.05)' : 'scale(1)',
                  transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)'
                }}
              >
                {isSignup ? 'Sign In' : 'Signup'}
              </button>
            </p>
          </div>
        </div>
      </div>

      {/* Admin Login Modal */}
      {showAdminModal && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: 'rgba(0,0,0,0.5)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div style={{
            backgroundColor: 'white',
            borderRadius: '12px',
            padding: '2rem',
            width: '400px',
            maxWidth: '90vw',
            boxShadow: '0 20px 25px rgba(0,0,0,0.3)'
          }}>
            <div style={{
              textAlign: 'center',
              marginBottom: '1.5rem'
            }}>
              <h3 style={{
                fontSize: '1.5rem',
                fontWeight: '600',
                color: '#1f2937',
                marginBottom: '0.5rem'
              }}>ðŸ‘‘ Admin Login</h3>
              <p style={{
                color: '#6b7280',
                fontSize: '0.875rem'
              }}>Enter admin credentials to access dashboard</p>
            </div>

            <form onSubmit={handleAdminLogin}>
              <div style={{ marginBottom: '1rem' }}>
                <label style={{
                  display: 'block',
                  fontSize: '0.875rem',
                  fontWeight: '500',
                  color: '#374151',
                  marginBottom: '0.5rem'
                }}>Username</label>
                <input
                  type="text"
                  value={adminCredentials.username}
                  onChange={(e) => setAdminCredentials({...adminCredentials, username: e.target.value})}
                  placeholder="Enter admin username"
                  required
                  style={{
                    width: '100%',
                    padding: '0.75rem',
                    border: '2px solid #e5e7eb',
                    borderRadius: '8px',
                    fontSize: '1rem',
                    outline: 'none'
                  }}
                />
              </div>

              <div style={{ marginBottom: '1rem' }}>
                <label style={{
                  display: 'block',
                  fontSize: '0.875rem',
                  fontWeight: '500',
                  color: '#374151',
                  marginBottom: '0.5rem'
                }}>Password</label>
                <input
                  type="password"
                  value={adminCredentials.password}
                  onChange={(e) => setAdminCredentials({...adminCredentials, password: e.target.value})}
                  placeholder="Enter admin password"
                  required
                  style={{
                    width: '100%',
                    padding: '0.75rem',
                    border: '2px solid #e5e7eb',
                    borderRadius: '8px',
                    fontSize: '1rem',
                    outline: 'none'
                  }}
                />
              </div>

              {adminError && (
                <div style={{
                  backgroundColor: '#fef2f2',
                  border: '1px solid #fecaca',
                  borderRadius: '8px',
                  padding: '0.75rem',
                  marginBottom: '1rem',
                  color: '#dc2626',
                  fontSize: '0.875rem',
                  textAlign: 'center'
                }}>
                  {adminError}
                </div>
              )}

              <div style={{
                display: 'flex',
                gap: '0.75rem'
              }}>
                <button
                  type="button"
                  onClick={() => {
                    setShowAdminModal(false);
                    setAdminCredentials({ username: '', password: '' });
                    setAdminError('');
                  }}
                  style={{
                    flex: 1,
                    padding: '0.75rem',
                    backgroundColor: '#6b7280',
                    color: 'white',
                    border: 'none',
                    borderRadius: '8px',
                    fontSize: '1rem',
                    cursor: 'pointer'
                  }}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={adminLoading}
                  style={{
                    flex: 1,
                    padding: '0.75rem',
                    backgroundColor: adminLoading ? '#9ca3af' : '#3b82f6',
                    color: 'white',
                    border: 'none',
                    borderRadius: '8px',
                    fontSize: '1rem',
                    cursor: adminLoading ? 'not-allowed' : 'pointer'
                  }}
                >
                  {adminLoading ? 'Logging in...' : 'Login'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Login;
