import React, { useState } from 'react';
import axios from 'axios';

const QuestionForm = ({ quizId, onQuestionAdded }) => {
  const [questionText, setQuestionText] = useState('');
  const [questionType, setQuestionType] = useState('MULTIPLE_CHOICE');
  const [options, setOptions] = useState([
    { optionText: '', isCorrect: false },
    { optionText: '', isCorrect: false }
  ]);
  const [message, setMessage] = useState('');
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    if (!questionText || questionText.length < 5 || questionText.length > 500) {
      newErrors.questionText = 'Question text must be between 5 and 500 characters.';
    }
    const correctOptions = options.filter(opt => opt.isCorrect);
    if (correctOptions.length !== 1) {
      newErrors.options = 'Each question must have exactly one correct option';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleOptionChange = (index, value) => {
    const newOptions = [...options];
    newOptions[index].optionText = value;
    setOptions(newOptions);
  };

  const handleCorrectOptionChange = (index) => {
    const newOptions = options.map((option, i) => ({
      ...option,
      isCorrect: i === index
    }));
    setOptions(newOptions);
  };

  const handleAddOption = () => {
    if (questionType === 'MULTIPLE_CHOICE') {
      setOptions([...options, { optionText: '', isCorrect: false }]);
    }
  };

  const handleRemoveOption = (index) => {
    if (options.length > 2) {
      setOptions(options.filter((_, i) => i !== index));
    }
  };

  const handleTypeChange = (e) => {
    setQuestionType(e.target.value);
    if (e.target.value === 'TRUE_FALSE') {
      setOptions([
        { optionText: 'True', isCorrect: false },
        { optionText: 'False', isCorrect: false }
      ]);
    } else {
      setOptions([
        { optionText: '', isCorrect: false },
        { optionText: '', isCorrect: false }
      ]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      setMessage('');
      return;
    }

    const newQuestion = {
      questionText,
      questionType,
      options,
    };

    try {
await axios.post(
`http://localhost:8080/api/quizzes/${quizId}/questions`,
newQuestion
);
setMessage('Question added!');
setQuestionText('');
setOptions(
questionType === 'TRUE_FALSE'
? [
{ optionText: 'True', isCorrect: false },
{ optionText: 'False', isCorrect: false }
]
: [
{ optionText: '', isCorrect: false },
{ optionText: '', isCorrect: false }
]
);
setErrors({});
onQuestionAdded();
} catch (err) {
// Fallback to localStorage when API fails
console.log('API failed, saving question to localStorage');
try {
  const quizzes = JSON.parse(localStorage.getItem('quizzes') || '[]');
  const quizIndex = quizzes.findIndex(q => q.id === quizId);

  if (quizIndex !== -1) {
    if (!quizzes[quizIndex].questions) {
      quizzes[quizIndex].questions = [];
    }

    const questionId = Date.now().toString();
    const questionWithId = { ...newQuestion, id: questionId };
    quizzes[quizIndex].questions.push(questionWithId);

    localStorage.setItem('quizzes', JSON.stringify(quizzes));

    // Dispatch event to update student dashboard
    window.dispatchEvent(new Event('questionsUpdated'));

    setMessage('Question added!');
    setQuestionText('');
    setOptions(
      questionType === 'TRUE_FALSE'
        ? [
            { optionText: 'True', isCorrect: false },
            { optionText: 'False', isCorrect: false }
          ]
        : [
            { optionText: '', isCorrect: false },
            { optionText: '', isCorrect: false }
          ]
    );
    setErrors({});
    onQuestionAdded();
  } else {
    setMessage('Quiz not found');
  }
} catch (localErr) {
  const apiErrors = err.response?.data?.errors || ['An unexpected error occurred.'];
  setMessage(Array.isArray(apiErrors) ? apiErrors.join(', ') : apiErrors.toString());
}
}
};

return (
<div style={{
backgroundColor: 'white',
borderRadius: '12px',
padding: '2rem',
boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
margin: '1rem 0'
}}>
<h2 style={{
color: '#1f2937',
fontSize: '1.5rem',
fontWeight: '700',
marginBottom: '1.5rem',
textAlign: 'center'
}}>Add Question to Quiz</h2>
<form onSubmit={handleSubmit}>
<div style={{ marginBottom: '1.5rem' }}>
<label htmlFor="questionText" style={{
display: 'block',
fontWeight: '600',
color: '#374151',
marginBottom: '0.5rem'
}}>Question Text</label>
<input
type="text"
id="questionText"
value={questionText}
onChange={(e) => setQuestionText(e.target.value)}
placeholder="Enter your question"
style={{
width: '100%',
padding: '0.75rem',
border: '2px solid #d1d5db',
borderRadius: '8px',
fontSize: '1rem',
outline: 'none',
transition: 'border-color 0.2s',
boxSizing: 'border-box'
}}
onFocus={(e) => e.target.style.borderColor = '#3b82f6'}
onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
/>
{errors.questionText && <span style={{ color: '#ef4444', fontSize: '0.875rem' }}>{errors.questionText}</span>}
</div>

<div style={{ marginBottom: '1.5rem' }}>
<label htmlFor="questionType" style={{
display: 'block',
fontWeight: '600',
color: '#374151',
marginBottom: '0.5rem'
}}>Question Type</label>
<select id="questionType" value={questionType} onChange={handleTypeChange} style={{
width: '100%',
padding: '0.75rem',
border: '2px solid #d1d5db',
borderRadius: '8px',
fontSize: '1rem',
outline: 'none',
backgroundColor: 'white',
boxSizing: 'border-box'
}}>
<option value="MULTIPLE_CHOICE">Multiple Choice</option>
<option value="TRUE_FALSE">True/False</option>
</select>
</div>

<h3 style={{
color: '#374151',
fontSize: '1.125rem',
fontWeight: '600',
marginBottom: '1rem'
}}>Options</h3>
{options.map((option, index) => (
<div key={index} style={{
display: 'flex',
alignItems: 'center',
gap: '0.75rem',
marginBottom: '1rem',
padding: '0.75rem',
backgroundColor: '#f9fafb',
borderRadius: '8px',
border: '1px solid #e5e7eb'
}}>
<input
type="radio"
name="correctOption"
checked={option.isCorrect}
onChange={() => handleCorrectOptionChange(index)}
style={{
width: '18px',
height: '18px',
cursor: 'pointer'
}}
/>
<input
type="text"
value={option.optionText}
onChange={(e) => handleOptionChange(index, e.target.value)}
readOnly={questionType === 'TRUE_FALSE'}
maxLength={200}
placeholder={questionType === 'TRUE_FALSE' ? option.optionText : `Enter option ${index + 1}`}
style={{
flex: 1,
padding: '0.5rem',
border: '1px solid #d1d5db',
borderRadius: '6px',
fontSize: '0.875rem',
outline: 'none',
backgroundColor: questionType === 'TRUE_FALSE' ? '#f3f4f6' : 'white'
}}
/>
{questionType === 'MULTIPLE_CHOICE' && options.length > 2 && (
<button type="button" onClick={() => handleRemoveOption(index)} style={{
backgroundColor: '#ef4444',
color: 'white',
padding: '0.5rem',
border: 'none',
borderRadius: '6px',
cursor: 'pointer',
fontSize: '0.875rem'
}}>
√ó
</button>
)}
</div>
))}

<div style={{ marginBottom: '1.5rem' }}>
{questionType === 'MULTIPLE_CHOICE' && (
<button 
  type="button" 
  onClick={handleAddOption}
  style={{
    backgroundColor: '#10b981',
    color: 'white',
    padding: '0.75rem 1.5rem',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    marginRight: '1rem',
    fontSize: '1rem',
    fontWeight: '600',
    transition: 'background-color 0.2s'
  }}
  onMouseOver={(e) => e.target.style.backgroundColor = '#059669'}
  onMouseOut={(e) => e.target.style.backgroundColor = '#10b981'}
>
+ Add Option
</button>
)}

{errors.options && <span style={{ color: '#ef4444', fontSize: '0.875rem', display: 'block', marginTop: '0.5rem' }}>{errors.options}</span>}
</div>

<button 
  type="submit"
  style={{
    width: '100%',
    backgroundColor: '#3b82f6',
    color: 'white',
    padding: '0.875rem',
    border: 'none',
    borderRadius: '8px',
    cursor: 'pointer',
    fontSize: '1rem',
    fontWeight: '600',
    transition: 'background-color 0.2s'
  }}
  onMouseOver={(e) => e.target.style.backgroundColor = '#2563eb'}
  onMouseOut={(e) => e.target.style.backgroundColor = '#3b82f6'}
>
Add Question
</button>
</form>
{message && <p style={{
marginTop: '1rem',
padding: '0.75rem',
backgroundColor: message.includes('added') ? '#dcfce7' : '#fef2f2',
color: message.includes('added') ? '#065f46' : '#dc2626',
borderRadius: '6px',
textAlign: 'center'
}}>{message}</p>}
</div>
);
};

export default QuestionForm;
